public class Repeatedntimes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner (System.in);
		int n=3;
		
		
		for(int i=0;i<n;i++){
		    
		    int k=2*(i)+1;
		    for(int j=0;j<k;j++){
		        
		        System.out.print(1);
		        if(j!=k-1) {
		        	System.out.print(" ");
		        	
		        }
		        
		    }
		    
		    System.out.println();
		}
      
	}

}
